text = '';
pattern = 'adbbdfdsagdfrth';

x = zeros(1,15);
yb = zeros(1,15);
ykmp = zeros(1,15);
ybm =zeros(1,15);
for i = 1:15
    x(i) = i;
    [~, yb(i)] = BruteForce(pattern(1:i), text);
    [~, ykmp(i)] = KMP(pattern(1:i),text);
    ybm(i)=boyer_Moore(pattern(1:i),text);
end

plot(x, yb, x, ykmp,x,ybm);
legend('BruteForce', 'KMP','Boyer Moore');
xlabel('no. of matches');
ylabel('no. of comparisons');